// src/services/api.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:3000/api';

const fetchParticipants = () => axios.get(`${API_BASE_URL}/participants`);
const fetchPlayers = () => axios.get(`${API_BASE_URL}/players`);
const fetchTeams = () => axios.get(`${API_BASE_URL}/teams`);
const updatePlayer = (playerId, data) => axios.put(`${API_BASE_URL}/players/${playerId}`, data);
const createPick = (participantId, playerId, pickOrder) =>
  axios.post(`${API_BASE_URL}/picks`, { participantId, playerId, pickOrder });
const deletePick = (participantId, playerId) =>
  axios.delete(`${API_BASE_URL}/picks/${participantId}/${playerId}`);

export default {
  fetchParticipants,
  fetchPlayers,
  fetchTeams,
  updatePlayer,
  createPick,
  deletePick,
};
