// src/components/ui/ParticipantCard.js

import React, { useState } from 'react'; // Añadir useState aquí
import { Card, CardContent, CardHeader, CardTitle } from './card';
import './../styles/player-card.scss';
import { Button } from './button';

const ParticipantCard = ({ participant, players, onPick }) => {
  // Aquí puedes añadir cualquier estado necesario para manejar interacciones
  const [isConfirming, setIsConfirming] = useState(false);

  return (
    <Card className="participant-card p-4">
      <CardHeader className="mb-2">
        <CardTitle className="text-xl font-semibold">{participant.name}</CardTitle>
      </CardHeader>
      <CardContent className="space-y-2">
        {players.length > 0 ? (
          players.map(player => (
            <div key={player.id} className="item">
              <span
                className={`player-name ${player.selected ? 'player-selected' : ''}`}
                onClick={() => {
                  if (!player.selected) {
                    setIsConfirming(true); // Activar confirmación de selección
                  }
                }}
              >
                {player.name} ({player.position})
              </span>
              {isConfirming && (
                <div className="confirmation-buttons">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      onPick(player.id, participant.id);
                      setIsConfirming(false);
                    }}
                  >
                    Confirmar
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setIsConfirming(false)}
                  >
                    Cancelar
                  </Button>
                </div>
              )}
            </div>
          ))
        ) : (
          <div>No picks yet</div>
        )}
      </CardContent>
    </Card>
  );
};

export default ParticipantCard;
