// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import DraftScreen from './components/Draft';
import RosterScreen from './components/Roster';
import Clasificacion from './components/Clasificacion';
import Reglas from './components/Reglas';
import { FantasyProvider } from './context/FantasyContext'; // Proveedor del contexto
import './styles/global.scss'; // Estilos globales

function App() {
  return (
    <FantasyProvider>
      <Router>
        <Routes>
          <Route path="/draft" element={<DraftScreen />} />
          <Route path="/roster" element={<RosterScreen />} />
          <Route path="/clasificacion" element={<Clasificacion />} />
          <Route path="/reglas" element={<Reglas />} />
          {/* Puedes agregar más rutas aquí */}
        </Routes>
      </Router>
    </FantasyProvider>
  );
}

export default App;
