import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import Select from 'react-select'; // Asegúrate de tener react-select instalado correctamente.
import { useFantasy } from '../context/FantasyContext';
import './../styles/global.scss';
import './../styles/draft.scss';
import ConfirmationModal from './ui/ConfirmationModal';
import { ScrollArea } from './ui/scroll-area';
import { Button } from './ui/button';

export default function DraftScreen() {
  const { participants, players, teams, setParticipants, setPlayers } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  useEffect(() => {
    if (participants && participants.length > 0 && !selectedParticipant) {
      setSelectedParticipant(participants[0].id); // Seleccionar el primer participante por defecto
    }
  }, [participants]);

  const handlePickPlayer = async (player) => {
    if (player.selected || !selectedParticipant) return;

    setShowConfirmation(true);
    setSelectedPlayer(player);
  };

  const confirmPick = () => {
    if (!selectedPlayer || !selectedParticipant) return;

    setPlayers(
      players.map((p) =>
        p.id === selectedPlayer.id ? { ...p, selected: true, selectedBy: selectedParticipant } : p
      )
    );
    setParticipants(
      participants.map((p) =>
        p.id === selectedParticipant
          ? { ...p, picks: [...(p.picks ?? []), selectedPlayer] }
          : p
      )
    );

    setShowConfirmation(false);
    setSelectedPlayer(null);
    // Cambiar al siguiente participante
    setSelectedParticipant((prev) => (prev % participants.length) + 1);
  };

  const cancelPick = () => {
    setShowConfirmation(false);
    setSelectedPlayer(null);
  };

  return (
    <div className="container mx-auto p-4 draft-layout">
      <h1 className="text-2xl font-bold mb-4">Draft de Equipos</h1>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        {/* Selector de participante activo y lista de participantes */}
        <Card className="col-span-1 participants-card">
          <CardHeader>
            <CardTitle>Participantes</CardTitle>
          </CardHeader>
          <CardContent>
            <Select
              options={participants.map((participant) => ({
                value: participant.id,
                label: participant.name,
              }))}
              onChange={(selectedOption) =>
                setSelectedParticipant(selectedOption ? selectedOption.value : null)
              }
              placeholder="Seleccionar participante"
              value={
                selectedParticipant
                  ? {
                      value: selectedParticipant,
                      label: participants.find((p) => p.id === selectedParticipant)?.name,
                    }
                  : null
              }
              className="select-participant mb-4"
            />
            <ScrollArea className="h-[calc(100vh-300px)]">
              {participants.map((participant) => (
                <div key={participant.id} className="participant-card mb-2 p-2 border rounded">
                  <h3 className="font-bold">{participant.name}</h3>
                  <p className="text-sm">
                    Picks:{' '}
                    {(participant.picks ?? []).length > 0
                      ? participant.picks.map((p) => p.name).join(', ')
                      : 'No picks yet'}
                  </p>
                </div>
              ))}
            </ScrollArea>
          </CardContent>
        </Card>

        {/* Organizaciones y sus jugadores */}
        <Card className="col-span-3 teams-card">
          <CardHeader>
            <CardTitle>Organizaciones y Jugadores</CardTitle>
          </CardHeader>
          <CardContent>
            <ScrollArea className="h-[calc(100vh-200px)]">
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
                {teams.map((team) => (
                  <Card key={team.id} className="team-card">
                    <CardHeader>
                      <CardTitle>{team.name}</CardTitle>
                    </CardHeader>
                    <CardContent>
                      {players
                        .filter((player) => player.team_id === team.id)
                        .map((player) => (
                          <Button
                            key={player.id}
                            onClick={() => handlePickPlayer(player)}
                            className={`w-full mb-2 ${
                              player.selected ? 'bg-red-400 cursor-not-allowed' : 'bg-green-300'
                            }`}
                            disabled={player.selected}
                          >
                            {player.name} ({player.position})
                          </Button>
                        ))}
                    </CardContent>
                  </Card>
                ))}
              </div>
            </ScrollArea>
          </CardContent>
        </Card>
      </div>

      {showConfirmation && (
        <ConfirmationModal
          title="Confirmar Pick"
          message={`¿Estás seguro de que quieres seleccionar a ${selectedPlayer?.name}?`}
          onConfirm={confirmPick}
          onCancel={cancelPick}
        />
      )}
    </div>
  );
}
