// src/context/FantasyContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';

// Crear el contexto
const FantasyContext = createContext();

export const FantasyProvider = ({ children }) => {
  const [participants, setParticipants] = useState([]);
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const availablePlayers = players.filter(player => player.teamId !== 1);


  // Obtener participantes y jugadores al montar el proveedor
  useEffect(() => {
    const fetchParticipants = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/participants');
        if (!response.ok) throw new Error('Error al obtener los participantes');
        const data = await response.json();
        setParticipants(data);
      } catch (error) {
        console.error('Error fetching participants:', error);
      }
    };

    const fetchPlayers = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/players');
        if (!response.ok) throw new Error('Error al obtener los jugadores');
        const data = await response.json();
        setPlayers(data);
      } catch (error) {
        console.error('Error fetching players:', error);
      }
    };

    const fetchData = async () => {
      setLoading(true);
      await Promise.all([fetchParticipants(), fetchPlayers()]);
      setLoading(false);
    };

    fetchData();
  }, []);

  return (
    <FantasyContext.Provider value={{ participants, players, loading, setParticipants, setPlayers }}>
      {children}
    </FantasyContext.Provider>
  );
};

// Hook personalizado para usar el contexto
export const useFantasy = () => {
  return useContext(FantasyContext);
};
