import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import Select from 'react-select'; // Asegúrate de tener react-select correctamente instalado.
import { useFantasy } from '../context/FantasyContext';
import './../styles/global.scss';
import './../styles/draft.scss';
import ConfirmationModal from './ui/ConfirmationModal';

export default function DraftScreen() {
  const { participants, players, teams, setParticipants, setPlayers } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  useEffect(() => {
    if (participants && participants.length > 0 && !selectedParticipant) {
      setSelectedParticipant(participants[0].id); // Seleccionar el primer participante por defecto
    }
  }, [participants]);

  const handlePickPlayer = async (player) => {
    if (player.selected || !selectedParticipant) return;

    try {
      const participant = participants.find((p) => p.id === selectedParticipant);
      if (!participant) {
        alert('No participant selected');
        return;
      }

      if ((participant.picks ?? []).length >= 5) {
        alert('El participante ya tiene 5 jugadores seleccionados');
        return;
      }

      setShowConfirmation(true);
      setSelectedPlayer(player);
    } catch (error) {
      console.error('Error selecting player:', error);
      alert('Error selecting player. Please try again or check the server logs.');
    }
  };

  const confirmPick = async () => {
    if (!selectedPlayer || !selectedParticipant) return;

    try {
      const participant = participants.find((p) => p.id === selectedParticipant);
      const newPickOrder = (participant.picks ?? []).length + 1;

      // Guardar la selección en el estado local
      setPlayers(
        players.map((p) => (p.id === selectedPlayer.id ? { ...p, selected: true, selectedBy: selectedParticipant } : p))
      );
      setParticipants(
        participants.map((p) =>
          p.id === selectedParticipant ? { ...p, picks: [...(p.picks ?? []), { playerId: selectedPlayer.id, pickOrder: newPickOrder }] } : p
        )
      );
      setShowConfirmation(false);
      setSelectedPlayer(null);
    } catch (error) {
      console.error('Error confirming pick:', error);
      alert('Error confirming pick. Please try again or check the server logs.');
    }
  };

  const cancelPick = () => {
    setShowConfirmation(false);
    setSelectedPlayer(null);
  };

  return (
    <div className="draft-container">
      <div className="participants-section">
        <Card>
          <CardHeader>
            <CardTitle>Participantes</CardTitle>
          </CardHeader>
          <CardContent>
            <Select
              options={participants.map((participant) => ({
                value: participant.id,
                label: participant.name,
              }))}
              onChange={(selectedOption) => setSelectedParticipant(selectedOption ? selectedOption.value : null)}
              placeholder="Seleccionar participante"
              value={
                selectedParticipant
                  ? { value: selectedParticipant, label: participants.find((p) => p.id === selectedParticipant)?.name }
                  : null
              }
              className="select-participant"
            />
            <div className="participants-list">
              {participants.map((participant) => (
                <div key={participant.id} className="participant-card">
                  <h4>{participant.name}</h4>
                  <p>
                    Picks: {(participant.picks ?? []).length > 0 ? participant.picks.map((p) => p.playerId).join(', ') : 'No picks yet'}
                  </p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="teams-section">
        <div className="teams-grid">
          {teams.map((team) => (
            <Card key={team.id} className="team-card">
              <CardHeader>
                <CardTitle>{team.name}</CardTitle>
              </CardHeader>
              <CardContent>
                {players
                  .filter((player) => player.team_id === team.id)
                  .map((player) => (
                    <button
                      key={player.id}
                      className={`player-button ${player.selected ? 'selected' : 'available'}`}
                      onClick={() => handlePickPlayer(player)}
                    >
                      {player.name} ({player.position})
                    </button>
                  ))}
              </CardContent>
            </Card>
          ))}
        </div>
      </div>

      {showConfirmation && (
        <ConfirmationModal
          title="Confirmar Pick"
          message={`¿Estás seguro de que quieres seleccionar a ${selectedPlayer?.name}?`}
          onConfirm={confirmPick}
          onCancel={cancelPick}
        />
      )}
    </div>
  );
}
