// src/components/ui/ParticipantCard.js
import React, { useState } from 'react'; // Añadir `useState` aquí para evitar el error
import { Card, CardContent, CardHeader, CardTitle } from './card';
import './../styles/player-card.scss';

const ParticipantCard = ({ participant, players, onPick, onUnpick }) => {
  const [isHovered, setIsHovered] = useState(false); // Ejemplo de uso de useState
  
  return (
    <Card
      className="card p-4"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <CardHeader className="mb-2">
        <CardTitle className="text-xl font-semibold">{participant.name}</CardTitle>
      </CardHeader>
      <CardContent className="space-y-2">
        {players.length > 0 ? (
          players.map(player => (
            <div key={player.id} className="item">
              <span>{player.name} ({player.position})</span>
            </div>
          ))
        ) : (
          <div>No picks yet</div>
        )}
      </CardContent>
    </Card>
  );
};

export default ParticipantCard;
