// src/components/ui/ParticipantCard.js

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './card';
import { Button } from './button';

const ParticipantCard = ({ participant, players, onRelease }) => {
  const [showDetails, setShowDetails] = useState(false);

  return (
    <Card
      className="participant-card"
      onMouseEnter={() => setShowDetails(true)}
      onMouseLeave={() => setShowDetails(false)}
    >
      <CardHeader className="mb-2">
        <CardTitle className="text-xl font-semibold">{participant.name}</CardTitle>
      </CardHeader>
      <CardContent className="space-y-2">
        {players.length > 0 ? (
          players.map(player => (
            <div key={player.id} className={`item ${showDetails ? 'show' : ''}`}>
              <span>{player.name} ({player.position})</span>
              {showDetails && (
                <Button
                  variant="outline"
                  size="sm"
                  className="release-button"
                  onClick={() => onRelease(player.id, participant.id)}
                >
                  Release
                </Button>
              )}
            </div>
          ))
        ) : (
          <div>No picks yet</div>
        )}
      </CardContent>
    </Card>
  );
};

export default ParticipantCard;
