// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import DraftScreen from './components/Draft';
import RosterScreen from './components/Roster';
import Clasificacion from './components/Clasificacion';
import Reglas from './components/Reglas';
import { FantasyProvider } from './context/FantasyContext'; // Importar el proveedor del contexto
import 'react-toastify/dist/ReactToastify.css'; // Importa los estilos de react-toastify
import './styles/global.scss'; // Importa los estilos globales

function App() {
  return (
    // Envuelve toda la aplicación con el proveedor de contexto FantasyProvider
    <FantasyProvider>
      <Router>
        <Routes>
          <Route path="/draft" element={<DraftScreen />} />
          <Route path="/roster" element={<RosterScreen />} />
          <Route path="/clasificacion" element={<Clasificacion />} />
          <Route path="/reglas" element={<Reglas />} />
          {/* Añade aquí más rutas según sea necesario */}
        </Routes>
      </Router>
    </FantasyProvider>
  );
}

export default App;
