// src/components/Clasificacion.js

import React, { useState, useEffect } from 'react';
import { useFantasy } from '../context/FantasyContext';
import './../styles/clasificacion.scss'; // Añadir estilos para la clasificación

export default function ClasificacionScreen() {
  const { participants, players } = useFantasy();
  const [selectedParticipantId, setSelectedParticipantId] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (participants && players) {
      setLoading(false);
    } else {
      setError('No se han podido cargar los participantes o jugadores.');
      setLoading(true);
    }
  }, [participants, players]);

  if (loading) {
    return <div>Cargando clasificación...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // Ordenar participantes por la puntuación total de sus picks
  const getTotalPoints = (participant) => {
    return participant.picks.reduce((acc, pick) => {
      const player = players.find(p => p.id === pick.playerId);
      return acc + (player?.score || 0);
    }, 0);
  };

  const sortedParticipants = [...participants].sort((a, b) => {
    return getTotalPoints(b) - getTotalPoints(a);
  });

  return (
    <div className="clasificacion-screen">
      <h1 className="clasificacion-title">Clasificación de Participantes</h1>
      <div className="clasificacion-list">
        {sortedParticipants.map((participant) => (
          <div
            key={participant.id}
            className="participant-item"
            onMouseEnter={() => setSelectedParticipantId(participant.id)}
            onMouseLeave={() => setSelectedParticipantId(null)}
            onClick={() => setSelectedParticipantId(participant.id)}
          >
            <div className="participant-info">
              <span className="participant-name">{participant.name}</span>
              <span className="participant-score">Puntuación Total: {getTotalPoints(participant)}</span>
            </div>
            {selectedParticipantId === participant.id && (
              <div className="participant-details">
                <h3 className="details-title">Detalles de los Picks:</h3>
                <ul>
                  {participant.picks.map(pick => {
                    const player = players.find(p => p.id === pick.playerId);
                    return (
                      <li key={pick.playerId} className="pick-item">
                        {player ? (
                          <>
                            <span className="player-name">{player.name}</span>
                            <span className="player-position"> ({player.position})</span>
                            <span className="player-score"> - Puntuación: {player.score}</span>
                          </>
                        ) : (
                          <span>Jugador no encontrado</span>
                        )}
                      </li>
                    );
                  })}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
