// src/components/Draft.js
import React, { useState } from 'react';
import PlayerCard from './ui/PlayerCard';
import { useFantasy } from '../context/FantasyContext';

const DraftScreen = () => {
  const { players, setPlayers, participants } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);

  const handlePick = (playerId) => {
    if (!selectedParticipant) {
      alert("Please select a participant first");
      return;
    }

    // Update player as picked
    setPlayers(players.map((player) =>
      player.id === playerId ? { ...player, selected: true, selectedBy: selectedParticipant } : player
    ));
  };

  const handleUnpick = (playerId) => {
    setPlayers(players.map((player) =>
      player.id === playerId ? { ...player, selected: false, selectedBy: null } : player
    ));
  };

  return (
    <div className="draft-screen">
      {/* UI for selecting a participant */}
      <select onChange={(e) => setSelectedParticipant(e.target.value)} value={selectedParticipant || ''}>
        <option value="" disabled>Select a participant</option>
        {participants.map((participant) => (
          <option key={participant.id} value={participant.id}>{participant.name}</option>
        ))}
      </select>

      <div className="players-list">
        {players.map((player) => (
          <PlayerCard
            key={player.id}
            player={player}
            onPick={handlePick}
            onUnpick={handleUnpick}
          />
        ))}
      </div>
    </div>
  );
};

export default DraftScreen;
