// src/services/api.js

import axios from 'axios';

const API_URL = 'http://localhost:3000/api'; // Ajusta la URL según la configuración del servidor

const api = {
  fetchTeams: () => axios.get(`${API_URL}/teams`),
  fetchPlayers: () => axios.get(`${API_URL}/players`),
  fetchParticipants: () => axios.get(`${API_URL}/participants`),
  fetchPicks: () => axios.get(`${API_URL}/picks`),

  pickPlayer: (participantId, playerId, pickOrder) => {
    return axios.post(`${API_URL}/picks`, {
      participantId,
      playerId,
      pickOrder,
    }).then(response => {
      if (response.status === 200) {
        return response;
      } else {
        throw new Error('Error picking player');
      }
    }).catch(error => {
      console.error('Error in API pickPlayer:', error);
      throw error;
    });
  },

  releasePick: (participantId, playerId) => {
    return axios.delete(`${API_URL}/picks/${participantId}/${playerId}`)
      .then(response => {
        if (response.status === 200) {
          return response;
        } else {
          throw new Error('Error releasing pick');
        }
      }).catch(error => {
        console.error('Error in API releasePick:', error);
        throw error;
      });
  },
};

export default api;
