// src/components/Roster.js

import React, { useState, useEffect } from 'react';
import { useFantasy } from '../context/FantasyContext';
import { Button } from "./ui/button";
import './../styles/global.scss';
import './../styles/roster.scss';
import api from '../services/api';

export default function RosterScreen() {
  const { participants, players, loading, setParticipants } = useFantasy();
  const [currentParticipantIndex, setCurrentParticipantIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const participantsResponse = await api.fetchParticipants();
        const picksResponse = await api.fetchPicks();

        if (participantsResponse && participantsResponse.data) {
          // Actualizar los participantes con los picks de la base de datos
          const participantsWithPicks = participantsResponse.data.map(participant => {
            const participantPicks = picksResponse.data.filter(pick => pick.participant_id === participant.id);
            return {
              ...participant,
              picks: participantPicks.length > 0 ? participantPicks.map(pick => ({ playerId: pick.player_id, pickOrder: pick.pick_order })) : []
            };
          });
          setParticipants(participantsWithPicks);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [setParticipants]);

  // Si el estado de carga est치 activo o si los datos no est치n listos, mostrar mensaje de carga
  if (loading || !participants || participants.length === 0) {
    return <div>Loading participants...</div>;
  }

  const currentParticipant = participants[currentParticipantIndex];
  // Verificar si existen jugadores antes de proceder
  if (!currentParticipant || !players) {
    return <div>Loading data...</div>;
  }

  // Filtrar los jugadores seleccionados por el participante actual y asignarles su orden de pick
  const participantPlayers = currentParticipant.picks.map(pick => {
    const player = players.find(p => p.id === pick.playerId);
    return player ? { ...player, pickOrder: pick.pickOrder } : null;
  }).filter(player => player !== null);

  // Rellenar con jugadores predeterminados hasta que haya 5 en total
  const displayedPlayers = [...participantPlayers];
  while (displayedPlayers.length < 5) {
    displayedPlayers.push({
      id: `default-${displayedPlayers.length}`,
      name: "Jugador por defecto",
      position: "N/A",
      score: "N/A",
      image: "../assets/default.svg", // Imagen predeterminada
      pickOrder: displayedPlayers.length + 1, // Orden de pick ficticio para jugadores por defecto
    });
  }

  return (
    <div className="roster-container">
      {/* T칤tulo del Participante */}
      <div className="participant-header">
        <h1 className="participant-title">Equipo de {currentParticipant.name}</h1>
      </div>

      {/* Navegaci칩n entre participantes */}
      <div className="navigation-buttons">
        <Button
          onClick={() => setCurrentParticipantIndex((prev) => Math.max(prev - 1, 0))}
          disabled={currentParticipantIndex === 0}
          variant="outline"
          className="prev-button"
        >
          Participante Anterior
        </Button>
        <Button
          onClick={() => setCurrentParticipantIndex((prev) => Math.min(prev + 1, participants.length - 1))}
          disabled={currentParticipantIndex === participants.length - 1}
          variant="outline"
          className="next-button"
        >
          Siguiente Participante
        </Button>
      </div>

      {/* Jugadores del Equipo del Participante */}
      <div className="players-section">
        {displayedPlayers.map((player) => (
          <div key={player.id} className="player-card">
            <img
              src={player.image}
              alt={player.name}
              className="player-image"
            />
            <div className="player-info">
              <h3 className="player-name">{player.name}</h3>
              <p className="player-position">Posici칩n: {player.position}</p>
              <p className="player-score">
                Puntuaci칩n: {player.score}
              </p>
              {/* Mostrar el orden de pick del jugador */}
              {player.pickOrder && (
                <p className="player-pick-order">
                  Orden de Pick: #{player.pickOrder}
                </p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
