// src/components/Clasificacion.js

import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from './ui/card';
import { Button } from './ui/button';
import api from '../services/api';
import './../styles/global.scss'; // Asegura que los estilos globales están cargados.

export default function ClasificacionScreen() {
  const [participants, setParticipants] = useState([]);
  const [expandedParticipantId, setExpandedParticipantId] = useState(null);

  // Recuperar los datos de los participantes al cargar el componente
  useEffect(() => {
    const fetchClasificacion = async () => {
      try {
        const response = await api.fetchParticipantsWithPicks();
        setParticipants(response.data);
      } catch (error) {
        console.error('Error fetching participants:', error);
      }
    };

    fetchClasificacion();
  }, []);

  // Alternar el participante expandido al hacer clic en el nombre
  const toggleExpandParticipant = (participantId) => {
    setExpandedParticipantId(
      expandedParticipantId === participantId ? null : participantId
    );
  };

  return (
    <div className="container py-10 px-6">
      <h1 className="text-3xl font-bold text-center mb-6">Clasificación</h1>

      {/* Mapeo de los participantes */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {participants.map((participant, index) => (
          <Card key={participant.id} className="participant-card">
            <CardHeader
              className="cursor-pointer"
              onClick={() => toggleExpandParticipant(participant.id)}
            >
              <CardTitle className="text-xl font-semibold">
                {index + 1}. {participant.name}
              </CardTitle>
              <p className="text-gray-500">
                Puntos Totales: {participant.totalPoints || 0}
              </p>
            </CardHeader>

            <CardContent>
              {/* Mostrar los jugadores seleccionados en el draft */}
              <div className="picks-summary">
                <p className="font-medium">Picks:</p>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {participant.picks.length > 0 ? (
                    participant.picks.map((pick, idx) => (
                      <div key={idx} className="pick-item">
                        <span>{pick.player_name}</span>
                      </div>
                    ))
                  ) : (
                    <p>No picks seleccionados</p>
                  )}
                </div>
              </div>

              {/* Botón para expandir el detalle de los jugadores */}
              <Button
                variant="outline"
                className="mt-4"
                onClick={() => toggleExpandParticipant(participant.id)}
              >
                {expandedParticipantId === participant.id
                  ? 'Ocultar detalle'
                  : 'Mostrar detalle'}
              </Button>

              {/* Información detallada de cada jugador si el participante está expandido */}
              {expandedParticipantId === participant.id && (
                <div className="expanded-detail mt-4">
                  <p className="font-medium">Detalle de jugadores:</p>
                  <div className="mt-2">
                    {participant.picks.map((pick) => (
                      <div
                        key={pick.player_id}
                        className="flex justify-between p-2 border-b"
                      >
                        <span>{pick.player_name}</span>
                        <span>{pick.points} puntos</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
