// src/components/Clasificacion.js

import React, { useState, useEffect } from 'react';
import api from '../services/api'; // Asegúrate de que este import esté correcto

export default function Clasificacion() {
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch participantes y sus puntos
  useEffect(() => {
    const fetchClasificacion = async () => {
      try {
        const response = await api.fetchParticipantsWithPicks();
        setParticipants(response.data);
      } catch (error) {
        console.error('Error fetching participants:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchClasificacion();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="clasificacion-container">
      <h1>Clasificación</h1>
      {participants.length === 0 ? (
        <div>No hay participantes aún.</div>
      ) : (
        participants.map((participant, index) => (
          <div key={participant.id} className="participant-card">
            <h2>{index + 1}. {participant.name} (Total Puntos: {participant.totalPoints})</h2>
            <div>Picks:</div>
            <ul>
              {participant.picks.length > 0 ? (
                participant.picks.map((pickId, i) => (
                  <li key={pickId}>Pick {i + 1}: Jugador ID {pickId}</li>
                ))
              ) : (
                <li>Sin picks</li>
              )}
            </ul>
          </div>
        ))
      )}
    </div>
  );
}
