// src/components/ui/PlayerCard.js

import React, { useState } from 'react';
import '../../styles/styles/PlayerCard.scss';

const PlayerCard = ({ player, onPick }) => {
  const [isPicked, setIsPicked] = useState(player.selected || false);

  const handlePick = () => {
    if (typeof onPick === 'function') {
      onPick(player.id); // Llama a la función `onPick` pasada desde el componente padre
      setIsPicked(true);
    }
  };

  return (
    <div
      className={`player-card ${isPicked ? 'picked' : ''}`}
      onClick={handlePick} // Agrega el evento de clic
    >
      <h3>{player.name}</h3>
      <p>{player.position}</p>
    </div>
  );
};

export default PlayerCard;
