import React, { useState, useEffect } from 'react';
import TeamCard from "./ui/TeamCard";
import ParticipantCard from "./ui/ParticipantCard";
import { ScrollArea } from "./ui/scroll-area";
import Select from 'react-select';
import { useFantasy } from '../context/FantasyContext';
import './../styles/global.scss';
import api from '../services/api';

export default function DraftScreen() {
  const { players, setPlayers, participants, setParticipants, teams, setTeams } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);

  // Recuperar datos de equipos, jugadores y participantes, así como los picks cada vez que el componente se monta
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [teamsResponse, playersResponse, participantsResponse, picksResponse] = await Promise.all([
          api.fetchTeams(),
          api.fetchPlayers(),
          api.fetchParticipants(),
          api.fetchPicks(),
        ]);

        // Establecer equipos, jugadores y participantes con los datos recuperados
        setTeams(teamsResponse.data);
        setPlayers(playersResponse.data);

        // Crear un mapeo de participantes con sus picks correspondientes
        const participantsWithPicks = participantsResponse.data.map(participant => {
          const participantPicks = picksResponse.data.filter(pick => pick.participant_id === participant.id);
          return {
            ...participant,
            picks: participantPicks.length > 0 ? participantPicks.map(pick => ({ playerId: pick.player_id, pickOrder: pick.pick_order })) : []
          };
        });

        // Actualizar el estado de los jugadores para reflejar quiénes han sido seleccionados
        const updatedPlayers = playersResponse.data.map(player => {
          const pickedBy = participantsWithPicks.find(participant => participant.picks.some(p => p.playerId === player.id));
          return {
            ...player,
            selected: !!pickedBy,
            selectedBy: pickedBy ? pickedBy.id : null,
          };
        });

        setParticipants(participantsWithPicks);
        setPlayers(updatedPlayers);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    // Llamada inicial al cargar el componente
    fetchData();
  }, [setTeams, setPlayers, setParticipants]);

  // Function to release a player from a participant
  const releasePlayer = async (playerId, participantId) => {
    try {
      setPlayers(players.map(p => p.id === playerId ? { ...p, selected: false, selectedBy: null } : p));
      setParticipants(participants.map(p => p.id === participantId ? { ...p, picks: p.picks.filter(pick => pick.playerId !== playerId) } : p));

      // Guardar la liberación en la base de datos
      await api.releasePick(participantId, playerId);
    } catch (error) {
      console.error("Error releasing player:", error);
    }
  };

  return (
    <div className="container py-10 px-6">
      {/* Reemplazamos el SelectTrigger por react-select */}
      <div className="mb-6">
        <Select
          options={participants.map(participant => ({
            value: participant.id,
            label: participant.name
          }))}
          onChange={(selectedOption) => setSelectedParticipant(selectedOption ? selectedOption.value : null)}
          placeholder="Seleccionar participante"
          className="select-participant"
          isClearable
        />
      </div>

      {/* Render teams and participants */}
      <ScrollArea className="scroll-area">
        <div className="flex flex-wrap gap-4">
          {teams && teams.map(team => (
            <TeamCard
              key={team.id}
              team={team}
              players={players.filter(player => player.team_id === team.id)}
              onSelect={(player) => selectPlayer(player)}
              onRelease={(playerId, participantId) => releasePlayer(playerId, participantId)}
            />
          ))}
        </div>
      </ScrollArea>
    </div>
  );
}
