// src/components/Draft.js

import React, { useState, useEffect } from 'react';
import { useFantasy } from '../context/FantasyContext';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { ScrollArea } from './ui/scroll-area';
import './../styles/global.scss';
import './../styles/draft.scss';

export default function DraftScreen() {
  const { players, setPlayers, participants, teams } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setSelectedParticipant(participants.length > 0 ? participants[0].id : null);
  }, [participants]);

  const realizarPick = async (jugador) => {
    if (!selectedParticipant || jugador.selected) return;

    try {
      // Update backend to save the pick
      // Assume api.pickPlayer is a function that makes the pick request to the backend.
      await api.pickPlayer(selectedParticipant, jugador.id);

      // Update local state
      setPlayers(players.map(player => 
        player.id === jugador.id ? { ...player, selected: true, selectedBy: selectedParticipant } : player
      ));
    } catch (error) {
      console.error("Error selecting player:", error);
      alert('Error selecting player. Please try again.');
    }
  };

  const renderTeams = () => {
    return (
      <ScrollArea className="team-list-container">
        <div className="team-grid">
          {teams.map(team => (
            <Card key={team.id} className="team-card">
              <CardHeader className="team-header">
                <CardTitle>{team.name}</CardTitle>
              </CardHeader>
              <CardContent className="team-players">
                {players
                  .filter(player => player.team_id === team.id && !player.selected)
                  .map(player => (
                    <Button
                      key={player.id}
                      className="player-button"
                      onClick={() => realizarPick(player)}
                      disabled={participants.find(p => p.id === selectedParticipant)?.equipo.length >= 5}
                    >
                      {player.name}
                    </Button>
                  ))}
              </CardContent>
            </Card>
          ))}
        </div>
      </ScrollArea>
    );
  };

  if (loading) {
    return <div>Cargando datos...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="draft-screen">
      <div className="participant-select">
        <select
          value={selectedParticipant || ""}
          onChange={(e) => setSelectedParticipant(parseInt(e.target.value))}
        >
          <option value="" disabled>Seleccionar participante</option>
          {participants.map(participant => (
            <option key={participant.id} value={participant.id}>
              {participant.name}
            </option>
          ))}
        </select>
      </div>

      {renderTeams()}
    </div>
  );
}
