// src/components/Draft.js

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { ScrollArea } from "./ui/scroll-area";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select";
import { useFantasy } from '../context/FantasyContext';
import './styles/global.scss';  // Importa los estilos globales aquí

export default function Draft() {
  const { players, setPlayers, participants, setParticipants } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/players');
        if (!response.ok) throw new Error('Error al obtener los jugadores');
        const fetchedPlayers = await response.json();
        setPlayers(fetchedPlayers);
      } catch (error) {
        console.error("Error fetching players:", error);
      }
    };
    fetchPlayers();
  }, [setPlayers]);

  const selectPlayer = async (player) => {
    if (player.selected || !selectedParticipant) return;

    try {
      const updatedPlayers = players.map(p => 
        p.id === player.id ? { ...p, selected: true, selectedBy: selectedParticipant } : p
      );
      const updatedParticipants = participants.map(p => 
        p.id === selectedParticipant ? { ...p, picks: [...p.picks, player.id] } : p
      );

      setPlayers(updatedPlayers);
      setParticipants(updatedParticipants);

      await fetch(`http://localhost:3000/api/players/${player.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ selected: true, selectedBy: selectedParticipant })
      });
    } catch (error) {
      console.error("Error selecting player:", error);
    }
  };

  const releasePlayer = async (playerId, participantId) => {
    try {
      const updatedPlayers = players.map(p => 
        p.id === playerId ? { ...p, selected: false, selectedBy: null } : p
      );
      const updatedParticipants = participants.map(p => 
        p.id === participantId ? { ...p, picks: p.picks.filter(id => id !== playerId) } : p
      );

      setPlayers(updatedPlayers);
      setParticipants(updatedParticipants);

      await fetch(`http://localhost:3000/api/players/${playerId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ selected: false, selectedBy: null })
      });
    } catch (error) {
      console.error("Error releasing player:", error);
    }
  };

  const renderTeam = (teamId) => {
    if (!players) return null;

    const teamPlayers = players.filter(player => player.teamId === teamId);
    return (
      <Card key={teamId} className="card">
        <CardHeader className="card-header">
          <CardTitle className="card-title">Team {teamId}</CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          {teamPlayers.map(player => (
            <div 
              key={player.id} 
              className={`item ${player.selected ? 'opacity-50 bg-gray-200 cursor-not-allowed' : 'bg-white hover:bg-gray-100 cursor-pointer'}`}
              onClick={() => !player.selected && selectPlayer(player)}
            >
              <span className="text-gray-900 font-medium">{player.name} ({player.role})</span>
              {player.selected && (
                <Button 
                  variant="outline" 
                  size="sm"
                  className="release-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    releasePlayer(player.id, player.selectedBy);
                  }}
                >
                  Release
                </Button>
              )}
            </div>
          ))}
        </CardContent>
      </Card>
    );
  };

  const renderParticipants = () => {
    if (!participants || !players) {
      return <div>Loading participants...</div>;
    }

    return (
      <Card className="card mt-8">
        <CardHeader className="card-header border-b pb-4">
          <CardTitle className="card-title">Participants</CardTitle>
        </CardHeader>
        <CardContent className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mt-4">
          {participants.map((participant) => (
            <Card key={participant.id} className="card p-4">
              <CardHeader className="mb-2">
                <CardTitle className="text-xl font-semibold">{participant.name}</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                {participant.picks && participant.picks.length > 0 ? (
                  participant.picks.map(pickId => {
                    const player = players.find(p => p.id === pickId);
                    return player ? (
                      <div key={pickId} className="item">
                        <span>{player.name} ({player.role})</span>
                        <Button 
                          variant="outline" 
                          size="sm"
                          className="release-button"
                          onClick={() => releasePlayer(player.id, participant.id)}
                        >
                          Release
                        </Button>
                      </div>
                    ) : null;
                  })
                ) : (
                  <div>No picks yet</div>
                )}
              </CardContent>
            </Card>
          ))}
        </CardContent>
      </Card>
    );
  };

  return (
    <div className="container py-10 px-6">
      <div className="mb-6">
        <Select onValueChange={(value) => setSelectedParticipant(Number(value))}>
          <SelectTrigger className="w-[200px] bg-white border border-gray-300 rounded-md shadow-sm">
            <SelectValue placeholder="Select participant" />
          </SelectTrigger>
          <SelectContent className="bg-white border border-gray-300 rounded-md shadow-lg mt-2">
            {participants && participants.map((participant) => (
              <SelectItem key={participant.id} value={participant.id.toString()} className="hover:bg-gray-100 p-2">
                {participant.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>
      <ScrollArea className="scroll-area">
        <div className="flex flex-wrap gap-4">
          {Array(20).fill(null).map((_, i) => renderTeam(i + 1))}
        </div>
      </ScrollArea>
      {renderParticipants()}
    </div>
  );
}
