// src/components/Roster.js

import React, { useState, useEffect } from 'react';
import { useFantasy } from '../context/FantasyContext';
import { Button } from "./ui/button";
import './../styles/global.scss';
import './../styles/roster.scss';
import api from '../services/api';

export default function RosterScreen() {
  const { participants, players, setParticipants } = useFantasy();
  const [currentParticipantIndex, setCurrentParticipantIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [participantsResponse, picksResponse] = await Promise.all([
          api.fetchParticipants(),
          api.fetchPicks(),
        ]);

        // Actualizar los participantes con los picks de la base de datos
        const participantsWithPicks = participantsResponse.data.map(participant => {
          const picks = picksResponse.data.filter(pick => pick.participant_id === participant.id);
          return {
            ...participant,
            picks: picks.length > 0 ? picks.map(pick => pick.player_id) : [] // Asegurarse de que picks sea un array
          };
        });

        setParticipants(participantsWithPicks);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [setParticipants]);

  // Función para cambiar al siguiente participante
  const nextParticipant = () => {
    if (currentParticipantIndex < participants.length - 1) {
      setCurrentParticipantIndex(currentParticipantIndex + 1);
    }
  };

  // Función para volver al participante anterior
  const prevParticipant = () => {
    if (currentParticipantIndex > 0) {
      setCurrentParticipantIndex(currentParticipantIndex - 1);
    }
  };

  // Si no hay participantes, mostrar un mensaje de carga
  if (!participants || participants.length === 0) {
    return <div>Loading participants...</div>;
  }

  const currentParticipant = participants[currentParticipantIndex];
  // Asegúrate de que picks sea un array vacío si está indefinido
  const participantPlayers = players.filter(player => currentParticipant.picks && currentParticipant.picks.includes(player.id));

  return (
    <div className="roster-container">
      {/* Título del Participante */}
      <div className="participant-header">
        <h1 className="participant-title">Equipo de {currentParticipant.name}</h1>
      </div>

      {/* Navegación entre participantes */}
      <div className="navigation-buttons">
        <Button
          onClick={prevParticipant}
          disabled={currentParticipantIndex === 0}
          variant="outline"
          className="prev-button"
        >
          Participante Anterior
        </Button>
        <Button
          onClick={nextParticipant}
          disabled={currentParticipantIndex === participants.length - 1}
          variant="outline"
          className="next-button"
        >
          Siguiente Participante
        </Button>
      </div>

      {/* Jugadores del Equipo del Participante */}
      <div className="players-section">
        {participantPlayers.length > 0 ? (
          participantPlayers.map((player) => (
            <div key={player.id} className="player-card">
              <img
                src={player.image || '/placeholder.svg'}
                alt={player.name}
                className="player-image"
              />
              <div className="player-info">
                <h3 className="player-name">{player.name}</h3>
                <p className="player-position">{player.position}</p>
                <p className="player-score">
                  Puntuación: {player.score ? player.score : 'N/A'}
                </p>
              </div>
            </div>
          ))
        ) : (
          <div>Este participante no ha seleccionado jugadores.</div>
        )}
      </div>
    </div>
  );
}
