// src/context/FantasyContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../services/api'; // Import the API module for fetching data

// Create the context
const FantasyContext = createContext();

export const FantasyProvider = ({ children }) => {
  const [participants, setParticipants] = useState([]);
  const [players, setPlayers] = useState([]);
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch participants, players, and teams on provider mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Fetch participants, players, and teams simultaneously
        const [participantsData, playersData, teamsData] = await Promise.all([
          api.fetchParticipants(),
          api.fetchPlayers(),
          api.fetchTeams(),
        ]);

        setParticipants(participantsData.data || []);
        setPlayers(playersData.data || []);
        setTeams(teamsData.data || []);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <FantasyContext.Provider
      value={{
        participants,
        players,
        teams,
        loading,
        setParticipants,
        setPlayers,
        setTeams,
      }}
    >
      {children}
    </FantasyContext.Provider>
  );
};

// Custom hook to use the FantasyContext
export const useFantasy = () => {
  return useContext(FantasyContext);
};
