// src/components/ui/PlayerCard.js

import React, { useState } from 'react';
import ConfirmationModal from './ConfirmationModal';

const PlayerCard = ({ player, onPick, onRelease }) => {
  const [showModal, setShowModal] = useState(false);
  const [action, setAction] = useState('');

  const handlePickClick = () => {
    setAction('pick');
    setShowModal(true);
  };

  const handleReleaseClick = () => {
    setAction('release');
    setShowModal(true);
  };

  const handleConfirm = () => {
    if (action === 'pick') {
      onPick(player);
    } else if (action === 'release') {
      onRelease(player.id, player.selectedBy);
    }
    setShowModal(false);
  };

  const handleCancel = () => {
    setShowModal(false);
  };

  return (
    <>
      <div
        className={`player-card ${player.selected ? 'player-selected' : ''}`}
        onClick={() => (player.selected ? handleReleaseClick() : handlePickClick())}
      >
        <span
          className={`player-name ${player.selected ? 'player-selected-name' : ''}`}
        >
          {player.name} ({player.position})
        </span>
      </div>

      {showModal && (
        <ConfirmationModal
          title={action === 'pick' ? 'Confirmar Selección' : 'Confirmar Liberación'}
          message={
            action === 'pick'
              ? `¿Estás seguro de seleccionar a ${player.name}?`
              : `¿Estás seguro de liberar a ${player.name}?`
          }
          onConfirm={handleConfirm}
          onCancel={handleCancel}
        />
      )}
    </>
  );
};

export default PlayerCard;
