// src/components/Draft.js

import React, { useState, useEffect } from 'react';
import { useFantasy } from '../context/FantasyContext';
import api from '../services/api';
import './../styles/global.scss';
import './../styles/draft.scss';

export default function DraftScreen() {
  const { players, setPlayers, participants } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Recuperar datos de jugadores y equipos
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [playersResponse, picksResponse] = await Promise.all([
          api.fetchPlayers(),
          api.fetchPicks(),
        ]);

        // Actualizar el estado de los jugadores para reflejar quiénes han sido seleccionados
        const updatedPlayers = playersResponse.data.map(player => {
          const picked = picksResponse.data.find(pick => pick.player_id === player.id);
          return {
            ...player,
            selected: !!picked,
            selectedBy: picked ? picked.participant_id : null,
          };
        });

        setPlayers(updatedPlayers);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error al cargar los datos. Por favor intenta de nuevo.');
        setLoading(false);
      }
    };

    fetchData();
  }, [setPlayers]);

  // Function to select a player for a participant
  const selectPlayer = async (player) => {
    if (player.selected || !selectedParticipant) return;

    try {
      // Guardar la selección en la base de datos antes de actualizar el estado local
      await api.pickPlayer(selectedParticipant, player.id);

      // Actualiza el estado en el frontend para reflejar la selección
      setPlayers(players.map(p => p.id === player.id ? { ...p, selected: true, selectedBy: selectedParticipant } : p));

    } catch (error) {
      console.error("Error selecting player:", error);
      alert('Error selecting player. Please try again.');
    }
  };

  const renderTeams = () => {
    const teams = [...new Set(players.map(player => player.team_name))]; // Obtener equipos únicos

    return (
      <div className="draft-table">
        <div className="header">
          {teams.map(team => (
            <div key={team} className="team-column-header">{team}</div>
          ))}
        </div>
        <div className="body">
          {teams.map(team => (
            <div key={team} className="team-column">
              {players
                .filter(player => player.team_name === team)
                .map(player => (
                  <div
                    key={player.id}
                    className={`player-cell ${player.selected ? 'selected' : 'available'}`}
                    onClick={() => !player.selected && selectPlayer(player)}
                  >
                    {player.name} ({player.role})
                  </div>
                ))}
            </div>
          ))}
        </div>
      </div>
    );
  };

  if (loading) {
    return <div>Cargando datos...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="draft-screen">
      <div className="participant-select">
        <select
          value={selectedParticipant || ""}
          onChange={(e) => setSelectedParticipant(e.target.value)}
        >
          <option value="" disabled>Seleccionar participante</option>
          {participants.map(participant => (
            <option key={participant.id} value={participant.id}>
              {participant.name}
            </option>
          ))}
        </select>
      </div>

      <div className="teams-container">
        {renderTeams()}
      </div>
    </div>
  );
}
