// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Draft from './components/Draft';
import Roster from './components/Roster';
import Home from './components/Home';
import Clasificacion from './components/Clasificacion';
import Reglas from './components/Reglas';
import { FantasyProvider } from './context/FantasyContext'; // Importar el proveedor del contexto
import 'react-toastify/dist/ReactToastify.css'; // Importa los estilos
import './styles/global.scss';

const App = () => {
  return (
    <FantasyProvider> {/* Envolver la aplicaci√≥n con el proveedor */}
      <Router>
        <Header />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/draft" element={<DraftScreen />} />
          <Route path="/roster" element={<RosterScreen />} />
          <Route path="/clasificacion" element={<Clasificacion />} />
          <Route path="/reglas" element={<Reglas />} />
        </Routes>
      </Router>
    </FantasyProvider>
  );
};


export default App;
