// src/components/Roster.js

import React, { useState } from 'react';
import { useFantasy } from '../context/FantasyContext';
import { Button } from "./ui/button";
import './../styles/global.scss';
import './../styles/roster.scss';
import api from '../services/api';

export default function RosterScreen() {
  const { teams } = useFantasy();
  const [currentTeamIndex, setCurrentTeamIndex] = useState(0);

  // Función para cambiar al siguiente equipo
  const nextTeam = () => {
    if (currentTeamIndex < teams.length - 1) {
      setCurrentTeamIndex(currentTeamIndex + 1);
    }
  };

  // Función para volver al equipo anterior
  const prevTeam = () => {
    if (currentTeamIndex > 0) {
      setCurrentTeamIndex(currentTeamIndex - 1);
    }
  };

  // Si no hay equipos, mostrar un mensaje de carga
  if (!teams || teams.length === 0) {
    return <div>Loading teams...</div>;
  }

  const currentTeam = teams[currentTeamIndex];

  return (
    <div className="roster-container">
      {/* Título del Equipo */}
      <div className="team-header">
        <h1 className="team-title">{currentTeam.name}</h1>
      </div>

      {/* Navegación entre equipos */}
      <div className="navigation-buttons">
        <Button
          onClick={prevTeam}
          disabled={currentTeamIndex === 0}
          variant="outline"
          className="prev-button"
        >
          Equipo Anterior
        </Button>
        <Button
          onClick={nextTeam}
          disabled={currentTeamIndex === teams.length - 1}
          variant="outline"
          className="next-button"
        >
          Siguiente Equipo
        </Button>
      </div>

      {/* Jugadores del Equipo */}
      <div className="players-section">
        {currentTeam.players.length > 0 ? (
          currentTeam.players.map((player) => (
            <div key={player.id} className="player-card">
              <img
                src={player.image || '/placeholder.svg'}
                alt={player.name}
                className="player-image"
              />
              <div className="player-info">
                <h3 className="player-name">{player.name}</h3>
                <p className="player-position">{player.position}</p>
                <p className="player-score">
                  Puntuación: {player.score ? player.score : 'N/A'}
                </p>
              </div>
            </div>
          ))
        ) : (
          <div>No hay jugadores asignados a este equipo.</div>
        )}
      </div>
    </div>
  );
}
