// src/components/ui/TeamCard.js

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './card';
import PlayerCard from './PlayerCard';

export default function TeamCard({ team, players, onSelect, onRelease }) {
  const teamPlayers = players.filter(player => player.team_id === team.id);

  return (
    <Card key={team.id} className="card">
      <CardHeader className="card-header">
        <CardTitle className="card-title">{team.name}</CardTitle>
      </CardHeader>
      <CardContent className="space-y-3">
        {teamPlayers.map(player => (
          <PlayerCard 
            key={player.id} 
            player={player} 
            onSelect={onSelect} 
            onRelease={onRelease} 
          />
        ))}
      </CardContent>
    </Card>
  );
}
