// src/components/ui/ParticipantCard.js

import React from 'react';
import { Card, CardHeader, CardContent, CardTitle } from "./card";
import { Button } from "./button";

const ParticipantCard = ({ participant, players, onRelease }) => {
  return (
    <Card className="participant-card">
      <CardHeader>
        <CardTitle>{participant.name}</CardTitle>
      </CardHeader>
      <CardContent>
        {participant.picks && participant.picks.length > 0 ? (
          participant.picks.map(pickId => {
            const player = players.find(p => p.id === pickId);
            return player ? (
              <div key={pickId} className="picked-player">
                <span>{player.name} ({player.position})</span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onRelease(player.id, participant.id)}
                >
                  Liberar
                </Button>
              </div>
            ) : null;
          })
        ) : (
          <div>No hay picks a√∫n</div>
        )}
      </CardContent>
    </Card>
  );
};

export default ParticipantCard;
