// src/components/ui/PlayerCard.js

import React, { useState } from 'react'; // Añade useState aquí
import { Button } from "./button";
import '../../styles/styles/PlayerCard.scss';

const PlayerCard = ({ player, onPick, onUnpick }) => {
  const [isPicking, setIsPicking] = useState(false); // useState debe ser importado para poder usarlo aquí
  const [isUnpicking, setIsUnpicking] = useState(false);

  const handlePick = () => {
    setIsPicking(true);
    onPick(player.id)
      .then(() => {
        setIsPicking(false);
      })
      .catch(() => {
        setIsPicking(false);
      });
  };

  const handleUnpick = () => {
    setIsUnpicking(true);
    onUnpick(player.id)
      .then(() => {
        setIsUnpicking(false);
      })
      .catch(() => {
        setIsUnpicking(false);
      });
  };

  return (
    <div
      className={`player-card ${player.selected ? 'player-selected' : ''}`}
      onClick={() => {
        if (!player.selected) {
          handlePick();
        } else {
          handleUnpick();
        }
      }}
    >
      <span className="player-name">{player.name} ({player.position})</span>
      {player.selected && (
        <Button
          variant="outline"
          size="sm"
          className={`release-button ${isUnpicking ? 'loading' : ''}`}
          onClick={(e) => {
            e.stopPropagation();
            handleUnpick();
          }}
        >
          {isUnpicking ? 'Releasing...' : 'Liberar'}
        </Button>
      )}
    </div>
  );
};

export default PlayerCard;
