import React, { useState } from 'react';
import { Button } from "./button";
import ConfirmationModal from './ConfirmationModal'; // Si tienes un modal para confirmar el pick

const PlayerCard = ({ player, onSelect, onPick, onRelease }) => {
  const [showConfirmModal, setShowConfirmModal] = useState(false);

  const handleSelect = () => {
    // Mostrar el modal de confirmación cuando intentas seleccionar un jugador
    setShowConfirmModal(true);
  };

  const handleConfirmPick = () => {
    // Confirmar el pick si onPick es una función
    if (typeof onPick === 'function') {
      onPick(player);
    }
    setShowConfirmModal(false);
  };

  const handleCancelPick = () => {
    // Cancelar el pick
    setShowConfirmModal(false);
  };

  return (
    <div
      className={`player-card ${player.selected ? 'player-selected' : ''}`}
      onClick={() => !player.selected && handleSelect()}
    >
      <span>{player.name} ({player.position})</span>

      {showConfirmModal && (
        <ConfirmationModal
          message={`¿Estás seguro de seleccionar a ${player.name}?`}
          onConfirm={handleConfirmPick}
          onCancel={handleCancelPick}
        />
      )}

      {player.selected && (
        <Button
          variant="outline"
          size="sm"
          className="release-button"
          onClick={(e) => {
            e.stopPropagation();
            if (typeof onRelease === 'function') {
              onRelease(player.id, player.selectedBy);
            }
          }}
        >
          Liberar
        </Button>
      )}
    </div>
  );
};

export default PlayerCard;
