// src/services/api.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:3001/api';

const api = {
  fetchTeams: () => axios.get(`${API_BASE_URL}/teams`),
  fetchPlayers: () => axios.get(`${API_BASE_URL}/players`),
  updatePlayer: (playerId, data) => axios.put(`${API_BASE_URL}/players/${playerId}`, data),
  // Puedes agregar más métodos aquí si necesitas otros endpoints
};

export default api;
