// src/components/ui/PlayerCard.js

import React from 'react';
import { Button } from "./button";

const PlayerCard = ({ player, onSelect, onRelease }) => {
  return (
    <div
      className={`player-card ${player.selected ? 'player-selected' : ''}`}
      onClick={() => !player.selected && onSelect(player)}
    >
      <span>{player.name} ({player.position})</span>
      {player.selected && (
        <Button
          variant="outline"
          size="sm"
          className="release-button"
          onClick={(e) => {
            e.stopPropagation();
            onRelease(player.id, player.selectedBy);
          }}
        >
          Liberar
        </Button>
      )}
    </div>
  );
};

export default PlayerCard;
