// src/components/Draft.js

import React, { useState, useEffect } from 'react';
import TeamCard from "./ui/TeamCard";
import ParticipantCard from "./ui/ParticipantCard";
import { ScrollArea } from "./ui/scroll-area";
import Select from 'react-select';
import { useFantasy } from '../context/FantasyContext';
import './../styles/global.scss'; // Ajusta la ruta para ir al directorio correcto
import api from '../services/api';

export default function DraftScreen() {
  const { players, setPlayers, participants, setParticipants, teams, setTeams } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);

  // Recuperar datos de equipos, jugadores y participantes, así como los picks cada vez que el componente se monta
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [teamsResponse, playersResponse, participantsResponse, picksResponse] = await Promise.all([
          api.fetchTeams(),
          api.fetchPlayers(),
          api.fetchParticipants(),
          api.fetchPicks(),
        ]);

        // Establecer equipos, jugadores y participantes con los datos recuperados
        setTeams(teamsResponse.data);
        setPlayers(playersResponse.data);

        // Crear un mapeo de participantes con sus picks correspondientes
        const participantsWithPicks = participantsResponse.data.map(participant => {
          const picks = picksResponse.data.filter(pick => pick.participant_id === participant.id);
          return {
            ...participant,
            picks: picks.length > 0 ? picks.map(pick => pick.player_id) : [] // Asegurarse de que siempre sea un array
          };
        });

        // Actualizar el estado de los jugadores para reflejar quiénes han sido seleccionados
        const updatedPlayers = playersResponse.data.map(player => {
          const pickedBy = participantsWithPicks.find(participant => participant.picks.includes(player.id));
          return {
            ...player,
            selected: !!pickedBy,
            selectedBy: pickedBy ? pickedBy.id : null,
          };
        });

        setParticipants(participantsWithPicks);
        setPlayers(updatedPlayers);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    // Llamada inicial al cargar el componente
    fetchData();
  }, [setTeams, setPlayers, setParticipants]);

  // Function to select a player for a participant
  const selectPlayer = async (player) => {
    // Verificar si ya hay un participante seleccionado
    if (!selectedParticipant) {
      alert('Por favor selecciona un participante primero');
      return;
    }

    // Verificar si el jugador ya está seleccionado
    if (player.selected) {
      alert('Este jugador ya ha sido seleccionado');
      return;
    }

    // Verificar si el participante tiene menos de 5 jugadores seleccionados
    const participant = participants.find(p => p.id === selectedParticipant);
    
    // Manejo seguro de `picks` - si `picks` está `undefined`, lo tratamos como un array vacío
    if (!participant.picks) participant.picks = [];

    if (participant.picks.length >= 5) {
      alert('El participante ya tiene 5 jugadores seleccionados');
      return;
    }

    try {
      // Actualiza el estado en el frontend para reflejar la selección
      const updatedPlayers = players.map(p =>
        p.id === player.id ? { ...p, selected: true, selectedBy: selectedParticipant } : p
      );
      setPlayers(updatedPlayers);

      const updatedParticipants = participants.map(p =>
        p.id === selectedParticipant ? { ...p, picks: [...p.picks, player.id] } : p
      );
      setParticipants(updatedParticipants);

      // Guardar la selección en la base de datos
      await api.pickPlayer(selectedParticipant, player.id);
    } catch (error) {
      console.error("Error selecting player:", error);
    }
  };

  // Function to release a player from a participant
  const releasePlayer = async (playerId, participantId) => {
    try {
      // Actualiza el estado en el frontend para reflejar la liberación
      const updatedPlayers = players.map(p =>
        p.id === playerId ? { ...p, selected: false, selectedBy: null } : p
      );
      setPlayers(updatedPlayers);

      const updatedParticipants = participants.map(p =>
        p.id === participantId ? { ...p, picks: p.picks.filter(id => id !== playerId) } : p
      );
      setParticipants(updatedParticipants);

      // Guardar la liberación en la base de datos
      await api.releasePick(participantId, playerId);
    } catch (error) {
      console.error("Error releasing player:", error);
    }
  };

  // Render team card with players belonging to the specific team
  const renderTeam = (team) => {
    if (!players) return null;

    const teamPlayers = players.filter(player => player.team_id === team.id);
    return (
      <TeamCard
        key={team.id}
        team={team}
        players={teamPlayers}
        onSelect={selectPlayer}
        onRelease={releasePlayer}
      />
    );
  };

  // Render participant card with the players they have selected
  const renderParticipants = () => {
    if (!participants || !players) {
      return <div>Loading participants...</div>;
    }

    return (
      <div className="participants-section mt-8">
        {participants.map((participant) => (
          <ParticipantCard
            key={participant.id}
            participant={participant}
            players={players.filter(player => participant.picks?.includes(player.id))} // Manejo seguro de 'picks'
            onRelease={releasePlayer}
          />
        ))}
      </div>
    );
  };

  return (
    <div className="container py-10 px-6">
      <div className="mb-6">
        <Select
          options={participants.map(participant => ({
            value: participant.id,
            label: participant.name
          }))}
          onChange={(selectedOption) => setSelectedParticipant(selectedOption ? selectedOption.value : null)}
          placeholder="Seleccionar participante"
          className="select-participant"
          isClearable
        />
      </div>

      <ScrollArea className="scroll-area">
        <div className="flex flex-wrap gap-4">
          {teams.map((team) => renderTeam(team))}
        </div>
      </ScrollArea>

      {renderParticipants()}
    </div>
  );
}
