// src/components/Draft.js
import React, { useState, useEffect } from 'react';
import TeamCard from "./ui/TeamCard";
import ParticipantCard from "./ui/ParticipantCard";
import { useFantasy } from '../context/FantasyContext';
import './../styles/draft.scss';
import { Select, SelectTrigger, SelectValue, SelectContent, SelectItem } from '@/components/ui/select';

const DraftScreen = () => {
  const { teams, players, participants, setParticipants } = useFantasy();
  const [selectedParticipant, setSelectedParticipant] = useState(null);

  useEffect(() => {
    // Set default participant if participants exist
    if (participants.length > 0) {
      setSelectedParticipant(participants[0].id);
    }
  }, [participants]);

  const handlePick = (playerId) => {
    setParticipants(prev => 
      prev.map(participant => 
        participant.id === selectedParticipant 
          ? {
              ...participant,
              picks: [...participant.picks, { playerId, pickOrder: participant.picks.length + 1 }]
            } 
          : participant
      )
    );
  };

  const handleUnpick = (playerId) => {
    setParticipants(prev =>
      prev.map(participant =>
        participant.id === selectedParticipant
          ? {
              ...participant,
              picks: participant.picks.filter(p => p.playerId !== playerId)
            }
          : participant
      )
    );
  };

  return (
    <div className="draft-screen">
      {/* Participants Selection */}
      <div className="participants-container">
        <Select
          value={selectedParticipant?.toString()}
          onValueChange={(value) => setSelectedParticipant(parseInt(value))}
        >
          <SelectTrigger className="participant-select">
            <SelectValue placeholder="Selecciona un participante" />
          </SelectTrigger>
          <SelectContent>
            {participants.map(participant => (
              <SelectItem key={participant.id} value={participant.id.toString()}>
                {participant.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <div className="participants-list">
          {participants.map(participant => (
            <ParticipantCard
              key={participant.id}
              participant={participant}
              players={players.filter(player => participant.picks?.some(p => p.playerId === player.id))}
              onRelease={handleUnpick}
            />
          ))}
        </div>
      </div>

      {/* Teams and Players Display */}
      <div className="teams-container">
        <div className="teams-grid">
          {teams.map(team => (
            <TeamCard
              key={team.id}
              team={team}
              players={players.filter(player => player.team_id === team.id)}
              onPick={handlePick}
              onUnpick={handleUnpick}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default DraftScreen;
