// src/components/Draft.js

import React, { useState, useEffect } from 'react';
import TeamCard from "./ui/TeamCard";
import ParticipantCard from "./ui/ParticipantCard";
import { ScrollArea } from "./ui/scroll-area";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select";
import { useFantasy } from '../context/FantasyContext';
import './../styles/global.scss'; // Asegúrate de que el archivo CSS esté disponible
import api from '../services/api'; // Archivo para manejar llamadas API

export default function DraftScreen() {
  const { players, setPlayers, participants, setParticipants } = useFantasy();
  const [teams, setTeams] = useState([]);
  const [selectedParticipant, setSelectedParticipant] = useState(null);

  // Obtener equipos, jugadores y participantes al montar el componente
  useEffect(() => {
    const fetchData = async () => {
      try {
        const teamsResponse = await fetch('http://localhost:3000/api/teams');
        const playersResponse = await fetch('http://localhost:3000/api/players');
        const participantsResponse = await fetch('http://localhost:3000/api/participants');

        if (!teamsResponse.ok || !playersResponse.ok || !participantsResponse.ok) {
          throw new Error('Error al obtener los datos');
        }

        const teamsData = await teamsResponse.json();
        const playersData = await playersResponse.json();
        const participantsData = await participantsResponse.json();

        setTeams(teamsData);
        setPlayers(playersData);
        setParticipants(participantsData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchData();
  }, [setPlayers, setParticipants]);

  // Seleccionar jugador
  const selectPlayer = async (player) => {
    if (player.selected || !selectedParticipant) return;

    // Verificar si el participante seleccionado ya tiene 5 jugadores
    const participant = participants.find(p => p.id === selectedParticipant);
    if (participant && participant.picks && participant.picks.length >= 5) {
      console.error("Este participante ya tiene 5 jugadores seleccionados");
      return;
    }

    try {
      // Actualizar estados localmente
      setPlayers(players.map(p => p.id === player.id ? { ...p, selected: true, selectedBy: selectedParticipant } : p));
      setParticipants(participants.map(p => p.id === selectedParticipant ? { ...p, picks: [...(p.picks || []), player.id] } : p));

      // Llamada para actualizar en la BDD
      await fetch(`http://localhost:3000/api/players/${player.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ selected: true, selectedBy: selectedParticipant })
      });
    } catch (error) {
      console.error("Error selecting player:", error);
    }
  };

  // Liberar jugador
  const releasePlayer = async (playerId, participantId) => {
    try {
      // Actualizar estados localmente
      setPlayers(players.map(p => p.id === playerId ? { ...p, selected: false, selectedBy: null } : p));
      setParticipants(participants.map(p => p.id === participantId ? { ...p, picks: (p.picks || []).filter(id => id !== playerId) } : p));

      // Llamada para actualizar en la BDD
      await fetch(`http://localhost:3000/api/players/${playerId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ selected: false, selectedBy: null })
      });
    } catch (error) {
      console.error("Error releasing player:", error);
    }
  };

  // Renderizar equipos y jugadores
  return (
    <div className="container py-10 px-6">
      {/* Seleccionar participante */}
      <div className="mb-6">
        <Select onValueChange={(value) => setSelectedParticipant(Number(value))}>
          <SelectTrigger className="select-trigger">
            <SelectValue placeholder="Seleccionar participante" />
          </SelectTrigger>
          <SelectContent className="select-content">
            {participants && participants.map((participant) => (
              <SelectItem key={participant.id} value={participant.id.toString()} className="select-item">
                {participant.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Mostrar equipos */}
      <ScrollArea className="scroll-area">
        <div className="flex flex-wrap gap-4">
          {teams.map(team => (
            <TeamCard
              key={team.id}
              team={team}
              players={players.filter(player => player.team_id === team.id)} // Filtrar jugadores del equipo
              onSelect={selectPlayer}
              onRelease={releasePlayer}
            />
          ))}
        </div>
      </ScrollArea>

      {/* Mostrar participantes y sus picks */}
      <div className="participants-container mt-8">
        {participants.map(participant => (
          <ParticipantCard
            key={participant.id}
            participant={participant}
            players={players.filter(player => (participant.picks || []).includes(player.id))} // Mostrar solo los jugadores seleccionados por el participante
            onRelease={releasePlayer}
          />
        ))}
      </div>
    </div>
  );
}
