import axios from 'axios';

const API_URL = 'http://localhost:3000/api'; // Asegúrate de que la URL sea correcta

const api = {
  fetchTeams: () => axios.get(`${API_URL}/teams`),
  fetchPlayers: () => axios.get(`${API_URL}/players`),
  fetchParticipants: () => axios.get(`${API_URL}/participants`),
  fetchPicks: () => axios.get(`${API_URL}/picks`),
  pickPlayer: (participantId, playerId, pickOrder) =>
    axios.post(`${API_URL}/picks`, { participantId, playerId, pickOrder }),
  releasePick: (participantId, playerId) =>
    axios.delete(`${API_URL}/picks/${participantId}/${playerId}`)
};

export default api;
