// src/components/Roster.js

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Avatar, AvatarImage, AvatarFallback } from "./ui/avatar";
import { useFantasy } from '../context/FantasyContext'; // Asegúrate de importar el hook personalizado desde el contexto

const positionColors = {
  Support: 'bg-blue-100 dark:bg-blue-800',
  Flex: 'bg-green-100 dark:bg-green-800',
  Entry: 'bg-red-100 dark:bg-red-800'
};

const Roster = () => {
  const { players, participants, loading } = useFantasy(); // Usar el hook personalizado para obtener datos del contexto

  // Mientras los datos se cargan
  if (loading) {
    return <div>Loading...</div>;
  }

  // En caso de que no haya datos disponibles aún
  if (!players || !participants) {
    return <div>No hay datos disponibles</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center">Roster</h1>

      {/* Renderizar participantes */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 mb-6">
        {participants.map((participant) => (
          <Card key={participant.id} className="transition-all hover:shadow-lg">
            <CardHeader>
              <CardTitle className="text-2xl text-center">{participant.name}</CardTitle>
            </CardHeader>
            <CardContent>
              {participant.picks && participant.picks.length > 0 ? (
                participant.picks.map((pickId) => {
                  const player = players.find(p => p.id === pickId);
                  return player ? (
                    <div key={pickId} className={`flex items-center mb-3 ${positionColors[player.position]}`}>
                      <Avatar className="w-12 h-12 mr-4">
                        <AvatarImage src={player.image} alt={player.name} />
                        <AvatarFallback>{player.name[0]}</AvatarFallback>
                      </Avatar>
                      <div>
                        <h3 className="font-bold text-lg">{player.name}</h3>
                        <p className="text-sm">Team: {player.team_id}</p>
                        <p className="text-sm">Position: {player.position}</p>
                      </div>
                    </div>
                  ) : (
                    <div key={pickId}>Jugador no encontrado</div>
                  );
                })
              ) : (
                <div>No hay jugadores seleccionados</div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Roster;
