// src/components/Roster.js

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Avatar, AvatarImage, AvatarFallback } from "./ui/avatar";
import './../styles/global.scss'; // Asegúrate de que la ruta es la correcta
import api from '../services/api'; // Importamos el archivo api para realizar las peticiones

export default function Roster() {
  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [loading, setLoading] = useState(true);

  // Colores para las posiciones de los jugadores
  const positionColors = {
    Support: 'bg-blue-100 dark:bg-blue-800',
    Flex: 'bg-green-100 dark:bg-green-800',
    Entry: 'bg-red-100 dark:bg-red-800'
  };

  // Obtener los equipos y los jugadores desde la base de datos
  useEffect(() => {
    const fetchTeamsAndPlayers = async () => {
      try {
        const teamsResponse = await api.fetchTeams();
        const playersResponse = await api.fetchPlayers();

        // Vincular jugadores con sus equipos
        const updatedTeams = teamsResponse.data.map(team => ({
          ...team,
          players: playersResponse.data.filter(player => player.team_id === team.id)
        }));

        setTeams(updatedTeams);
        setSelectedTeam(updatedTeams[0]); // Seleccionar el primer equipo por defecto
        setLoading(false);
      } catch (error) {
        console.error("Error fetching teams and players:", error);
        setLoading(false);
      }
    };

    fetchTeamsAndPlayers();
  }, []);

  // Función para seleccionar un equipo
  const handleTeamSelect = (team) => {
    setSelectedTeam(team);
  };

  if (loading) {
    return <div>Loading...</div>;
  }
  
  if (!players.length || !teams.length || !participants.length) {
    return <div>No hay datos disponibles</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center">Roster</h1>

      {/* Información del equipo seleccionado */}
      {selectedTeam && (
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="text-2xl">{selectedTeam.name}</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex justify-between items-center">
              <div className="text-lg">
                <span className="font-semibold">Ranking:</span> {selectedTeam.ranking || 'N/A'}
              </div>
              <div className="text-lg">
                <span className="font-semibold">Total Score:</span> {selectedTeam.totalScore || 0}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Cuadrícula de jugadores */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-4 mb-6">
        {selectedTeam && selectedTeam.players.map((player, index) => (
          <Card key={index} className={`${positionColors[player.position]} transition-all hover:shadow-lg`}>
            <CardHeader>
              <Avatar className="w-24 h-24 mx-auto">
                <AvatarImage src={player.image || "/placeholder.svg"} alt={player.name} />
                <AvatarFallback>{player.name[0]}</AvatarFallback>
              </Avatar>
            </CardHeader>
            <CardContent>
              <h3 className="font-bold text-lg text-center mb-2">{player.name}</h3>
              <div className="text-sm text-center">
                <p><span className="font-semibold">Team:</span> {selectedTeam.name}</p>
                <p><span className="font-semibold">Position:</span> {player.position}</p>
                <p><span className="font-semibold">Score:</span> {player.score}</p>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Botonera de selección de equipos */}
      <div className="flex flex-wrap gap-2 justify-center">
        {teams.map((team, index) => (
          <Button
            key={index}
            onClick={() => handleTeamSelect(team)}
            variant={selectedTeam && selectedTeam.id === team.id ? "default" : "outline"}
          >
            {team.name}
          </Button>
        ))}
      </div>
    </div>
  );
}
