// src/components/ui/PlayerCard.js

import React from 'react';
import { Button } from './button';
import '../../styles/ui/card.scss';

export default function PlayerCard({ player, onSelect, onRelease }) {
  return (
    <div 
      className={`item ${player.selected ? 'opacity-50 bg-gray-200 cursor-not-allowed' : 'bg-white hover:bg-gray-100 cursor-pointer'}`}
      onClick={() => !player.selected && onSelect(player)}
    >
      <span className="text-gray-900 font-medium">{player.name} ({player.position})</span>
      {player.selected && (
        <Button 
          variant="outline" 
          size="sm"
          className="release-button"
          onClick={(e) => {
            e.stopPropagation();
            onRelease(player.id, player.selectedBy);
          }}
        >
          Release
        </Button>
      )}
    </div>
  );
}
