// src/services/api.js

import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3000/api',
});

// Obtener los equipos
export const fetchTeams = () => api.get('/teams');

// Obtener los jugadores
export const fetchPlayers = () => api.get('/players');

// Obtener los participantes
export const fetchParticipants = () => api.get('/participants');

// Seleccionar un jugador para un participante (hacer un pick)
export const pickPlayer = (participantId, playerId) => {
  return api.post('/picks', {
    participantId,
    playerId,
    pickOrder: Date.now(), // Orden de pick basado en el timestamp para simplicidad
  });
};

// Liberar un jugador
export const releasePlayer = (participantId, playerId) => {
  return api.delete(`/picks/${participantId}/${playerId}`);
};

export default {
  fetchTeams,
  fetchPlayers,
  fetchParticipants,
  pickPlayer,
  releasePlayer,
};
