// src/context/FantasyContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../services/api'; // Importa el archivo de servicios para las llamadas API

// Crear el contexto
const FantasyContext = createContext();

export const FantasyProvider = ({ children }) => {
  const [participants, setParticipants] = useState([]);
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Obtener participantes y jugadores al montar el proveedor
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        
        // Hacer múltiples llamadas API de forma simultánea usando el archivo de servicios
        const [participantsResponse, playersResponse] = await Promise.all([
          api.fetchParticipants(),
          api.fetchPlayers(),
        ]);

        // Establecer los datos en los estados correspondientes
        setParticipants(participantsResponse.data);
        setPlayers(playersResponse.data);
      } catch (error) {
        console.error('Error fetching participants and players:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <FantasyContext.Provider value={{ participants, players, loading, setParticipants, setPlayers }}>
      {children}
    </FantasyContext.Provider>
  );
};

// Hook personalizado para usar el contexto
export const useFantasy = () => {
  return useContext(FantasyContext);
};
